! function() {
    "use strict";

    const zoom = 18;

    var map_sg = L.map('map-singapore').setView([1.2967417, 103.7866636], zoom);
    var map_my = L.map('map-malaysia').setView([3.0838205, 101.526588], zoom);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    maxZoom: 19,
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors, Tiles style by <a href="https://www.hotosm.org/" target="_blank">Humanitarian OpenStreetMap Team</a> hosted by <a href="https://openstreetmap.org/" target="_blank">OpenStreetMap</a>'
    }).addTo(map_sg);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    maxZoom: 19,
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors, Tiles style by <a href="https://www.hotosm.org/" target="_blank">Humanitarian OpenStreetMap Team</a> hosted by <a href="https://openstreetmap.org/" target="_blank">OpenStreetMap</a>'
    }).addTo(map_my);

    L.marker([1.2967417, 103.7866636]).addTo(map_sg).openPopup();
    L.marker([3.0838205, 101.526588]).addTo(map_my).openPopup();

}();
